package codeSignalPacka;

public class IsLucky {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n =103983;
		String int_n = String.valueOf(n);
		System.out.println("The length of n is "+int_n.length());
		System.out.println("The length of n is "+int_n.length()/2);
		int fi = Integer.parseInt(int_n);
		
		System.out.println(fi+1);
	}

}